import React,{useState, useEffect} from "react";
import axios from "axios";

function AddCart(){

  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [quentity, setQuentity] = useState("");
  
  const [carts, setCarts] = useState([]);

   useEffect(()=>{
      function getcarts(){
          axios.get("http://localhost:3000/carts").then((res)=>{
            setCarts(res.data)
          }).catch((err)=>{
              alert(err.message);
          })
      }
      getcarts();
   }, [])

  function passData(){
    
    const newCart = {

      name,
      price,
      quentity
    }
   
    axios.post("http://localhost:3000/carts", newCart).then(()=>{
         alert("Add Cart")
    }).catch((err)=>{
         alert(err)
    });
  }


    return(
        <div>
           
            <br/>
            <center>
             <form onSubmit={passData} style={{width:"400px", border:"2px solid green", padding:"50px"}}>
                 <center>
              <h1>Add To Cart</h1>
              </center>
             
              <label > Name :</label>  <br/>
              <input type="text" id="name" 
                   onChange={(e)=>{
                       setName(e.target.value);
              }} />
              <br/>

           
              <label >One Item price :</label>  <br/>
              <input type="text"  id="price" 
                   onChange={(e)=>{
                       setPrice(e.target.value);
              }} />
             <br/>

           
              <label >quentity :</label>  <br/>
              <input type="text"  id="quentity" 
                   onChange={(e)=>{
                       setQuentity(e.target.value);
              }} />
            <br/>

            
           <br/>
            <button >Add to Cart</button>
             </form>
             </center>
          <br/>

        <center>
          <h1>Your Product in cart</h1>
          <table className="table" style={{width:"600px"}}> 
               <thead> 
                <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Quentity</th>
                <th>Total</th>
                </tr>
                </thead>
       
            {
                    carts.map((json) => {
                    const {name, price, quentity, _id} = json
             return(      
               
                <tbody>
                <tr>
                <td>{name}</td>
                <td>{price}</td>
                <td>{quentity}</td>
                <td>{quentity*price}</td>

               
               </tr>
              
              </tbody>
              
             )
        })
    }     
    </table>
    </center>
        </div>
    )
}

export default AddCart;
